exports.cliVersion = '>=3.X';
var SILENT = true;

exports.init = function (logger, config, cli, appc) {
	var path = require('path'),
		fs = require('fs'),
		afs = appc.fs;

	function run(finished) {
		if (cli.argv['shallow'] === '') {
			logger.info('Not cleaning the Resources directory');
			finished();
			return;
		}
		var appDir = path.join(cli.argv['project-dir'], 'app');
		if (!afs.exists(appDir)) {
			logger.debug('Project not an Alloy app, exiting.');
			finished();
			return;
		}

		var resourcesDir = path.join(cli.argv['project-dir'], 'Resources');
		if (!afs.exists(resourcesDir)) {
			logger.debug('Resources directory does not exist.');
			finished();
			return;
		}
		rmdir(resourcesDir, fs, path, logger);
		logger.debug('Resources directory of %s has been emptied', appDir.cyan);
		finished();
	}

	cli.addHook('clean.post', function (build, finished) {
		run(finished);
	});

};

function rmdir(dirPath, fs, path, logger, removeSelf) {
	var files;
	try {
		files = fs.readdirSync(dirPath);
	} catch (e) {
		return;
	}
	if (files.length > 0) {
		for (var i = 0; i < files.length; i++) {
			var filePath = path.join(dirPath, files[i]);
			if (fs.statSync(filePath).isFile()) {
				fs.unlinkSync(filePath);
			} else {
				rmdir(filePath, fs, path, logger, true);
			}
		}
	}
	if (removeSelf) {
		fs.rmdirSync(dirPath);
	}
}

#!/usr/bin/env node

/*
	Reads the source map files (foo.js.map) for the Harness app
	and outputs the the generated and original line information
	for each map
*/

var async = require('async'),
	path = require('path'),
	platforms = require('../platforms'),
	fs = require('fs'),
	os = require('os'),
	SM = require('source-map'),
	_ = require('../Alloy/lib/alloy/underscore');

scan(path.join(__dirname, '..', 'test', 'projects', 'Harness', 'build', 'map', 'Resources'), '.map', function(err, files) {
	// Do something with files that ends in '.ext'.
	_.map(files, function(sourceMapFile) {
		console.log(path.basename(sourceMapFile));
		console.log('------------------------------');
		console.log('Generated line -> Original line');
		var sourceMap = fs.readFileSync(sourceMapFile, {encoding: 'utf8'}),
			lineArray = [];
		var smc = new SM.SourceMapConsumer(sourceMap);
		smc.eachMapping(function (m) {
			lineArray.push({gen: m.generatedLine, orig: m.originalLine});
		});
		var uniqueList = _.uniq(lineArray, function(item, key, gen) {
			return item.gen;
		});
		_.each(uniqueList, function(l) {
			var spacer = '                  '.slice(JSON.stringify(l.gen).length);
			console.log('   ' + l.gen + spacer + l.orig);
		});
		console.log('');
	});
});


function scan(dir, suffix, callback) {
	fs.readdir(dir, function(err, files) {
		var returnFiles = [];
		async.each(files, function(file, next) {
			var filePath = dir + '/' + file;
			fs.stat(filePath, function(err, stat) {
				if (err) {
					return next(err);
				}
				if (stat.isDirectory()) {
					scan(filePath, suffix, function(err, results) {
						if (err) {
							return next(err);
						}
						returnFiles = returnFiles.concat(results);
						next();
					});
				} else if (stat.isFile()) {
					if (file.indexOf(suffix, file.length - suffix.length) !== -1) {
						returnFiles.push(filePath);
					}
					next();
				}
			});
		}, function(err) {
			callback(err, returnFiles);
		});
	});
}

var async = require('async'),
	path = require('path'),
	exec = require('child_process').exec,
	platforms = require('../platforms'),
	_ = require('../Alloy/lib/alloy/underscore');

var harnessPath = path.join(__dirname, '..', 'test', 'projects', 'Harness');

async.series(_.map(platforms, function(p) {

	// return a function that compiles and copies generated code for the given app
	return function(callback) {

		// compile the app for all platforms
		var cmd = 'alloy compile --outputPath "' + harnessPath + '" --config platform=' + p.platform;
		console.log(cmd);
		exec(cmd, function(err, stdout, stderr) {
			if (err) { return callback(err); }

			// copy the generated controllers
			var src = path.join(harnessPath, 'Resources', p.titaniumFolder, 'alloy', 'controllers', '*');
			var dst = path.join(__dirname, '..', 'test', 'apps', process.argv[2], '_generated', p.platform, 'alloy', 'controllers');
			var cmd2 = 'cp -r ' + src + ' ' + dst + '/ && rm ' + dst + '/BaseController.js';
			console.log(cmd2);
			exec(cmd2, function(err, stdout, stderr) {
				return callback(err);
			});
		});
	};
}), function(err, results) {
	if (err) { throw new Error(err); }
});

var fs = require('fs'),
	path = require('path'),
	wrench = require('wrench'),
	platforms = require('../platforms/index'),
	_ = require('../Alloy/lib/alloy/underscore')._,
	colors = require('colors'),
	exec = require('child_process').exec,
	TESTS_WITH_DATES = ['ALOY-263', 'ALOY-1003', 'ALOY-1058'],
	TESTS_TO_SKIP = ['ALOY-840', 'ALOY-887', 'ALOY-932', 'ALOY-1080'];

var alloyRoot = path.join(__dirname, '..'),
	paths = {
		apps: path.join(alloyRoot, 'test', 'apps'),
		harness: path.join(alloyRoot, 'test', 'projects', 'Harness')
	},
	platformsArray = _.keys(platforms),
	platformCtr = 0;

// make sure we get a test app
var testApp = process.argv[2];
if (!testApp) {
	console.error('You must specify a test app');
	console.error('  ex. node testgen.js advanced/device_query');
	process.exit(1);
} else if (TESTS_TO_SKIP.indexOf(testApp) !== -1) {
	console.log((testApp + ' has code known to fail matching generated code. Canceling creation of _generated code.').yellow);
	process.exit();
} else if (TESTS_WITH_DATES.indexOf(testApp) !== -1) {
	console.log((testApp + ' contains date functions, which create localized code unlikely to match on other systems.').yellow);
	console.log(('Canceling creation of _generated code.').yellow);
	process.exit();
} else {
	console.log('generating code for ' + testApp.yellow);
}

// Stage the app
var cmd = 'jake app:setup dir=' + testApp + ' quiet=1';
exec(cmd, function(error, stdout, stderr) {
	if (error !== null) {
		console.error(error);
		console.error(stderr);
		process.exit(2);
	}

	doCompile(platformsArray[platformCtr++]);
});

function doCompile(platform) {
	if (!platform) { return; }

	exec('alloy compile ' + paths.harness + ' --config platform=' + platform, function(error, stdout, stderr) {
		if (error !== null) {
			console.error(error);
			console.error(stderr);
			process.exit(3);
		}

		var genDir = path.join(paths.apps, testApp, '_generated', platform);
		wrench.rmdirSyncRecursive(genDir, true);
		wrench.mkdirSyncRecursive(genDir, 0777);

		var locations = [
			path.join('alloy', 'controllers'),
			path.join('alloy', 'models'),
			path.join('alloy', 'widgets')
		];
		_.each(locations, function(l) {
			var src = path.join(paths.harness, 'Resources', (platform === 'ios' ? 'iphone' : platform), l);
			var dst = path.join(genDir, l);
			if (fs.existsSync(src) && fs.readdirSync(src).length !== 0) {
				wrench.mkdirSyncRecursive(dst, 0777);
				wrench.copyDirSyncRecursive(src, dst);

				// we don't need to evaluate BaseController.js every time
				var bc = path.join(dst, 'BaseController.js');
				if (l === path.join('alloy', 'controllers') && fs.existsSync(bc)) {
					fs.unlinkSync(bc);
				}

				console.log('Generated runtime files in ' +
					path.join('_generated', platform, l).cyan
				);
			}
		});

		doCompile(platformsArray[platformCtr++]);
	});
}
