	var actual = this.actual;
	var notText = this.isNot ? ' not' : '';
	this.message = function () {
		return 'Expected ' + actual + notText + ' to be a Javascript file';
	};

	try {
		var js = fs.readFileSync(this.actual, 'utf8');
		return toBeJavascript.call({actual:js}, expected);
	} catch (e) {
		return false;
	}
}

exports.addMatchers = function() {
	beforeEach(function() {
		this.addMatchers({
			toBeJavascript: toBeJavascript,
			toBeJavascriptFile: toBeJavascriptFile,
			toBeTssFile: toBeTssFile,
			toHaveNoUndefinedStyles: function() {
				return !_.find(this.actual, function(o) {
					return o.key === 'undefined' && o.isApi;
				});
			},
			toHaveSameContentAs: function(expected) {
				return U.normalizeReturns(fs.readFileSync(this.actual, 'utf8')) ===
					U.normalizeReturns(fs.readFileSync(expected, 'utf8'));
			},
			toExist: function(expected) {
				return path.existsSync(this.actual);
			},
			toBeArray: function(expected) {
				return _.isArray(this.actual);
			},
			toBeObject: function(expected) {
				return _.isObject(this.actual);
			}
		});
	});
};

var fs = require('fs'),
	path = require('path'),
	_ = require('../../lib/alloy/underscore')._,
	U = require('../../utils'),
	CU = require('./compilerUtils'),
	optimizer = require('./optimizer'),
	grammar = require('../../grammar/tss'),
	logger = require('../../logger'),
	BuildLog = require('./BuildLog'),
	CONST = require('../../common/constants'),
	deepExtend = require('node.extend');

// constants
var GLOBAL_STYLE_CACHE = 'global_style_cache.json';
var STYLE_ALLOY_TYPE = '__ALLOY_TYPE__';
var STYLE_EXPR_PREFIX = exports.STYLE_EXPR_PREFIX = '__ALLOY_EXPR__--';
var STYLE_REGEX = /^\s*([\#\.]{0,1})([^\[]+)(?:\[([^\]]+)\])*\s*$/;
var EXPR_REGEX = new RegExp('^' + STYLE_EXPR_PREFIX + '(.+)');
var BINDING_SPLIT_REGEX = /(\{[^:}]+\}(?!\}))/;
var BINDING_REFERENCE_REGEX = /^\{([^:}]+)\}$/;
var VALUES = {
	ID:     100000,
	CLASS:   10000,
	API:      1000,
	TSSIF:     500,
	PLATFORM:  100,
	FORMFACTOR: 10,
	SUM:         1,
	THEME:       0.9,
	ORDER:       0.0001
};
var DATEFIELDS = [
	'minDate', 'value', 'maxDate'
];
var KEYBOARD_TYPES = [
	'DEFAULT', 'ASCII', 'NUMBERS_PUNCTUATION', 'URL', 'EMAIL', 'DECIMAL_PAD', 'NAMEPHONE_PAD',
	'NUMBER_PAD', 'PHONE_PAD'
];
var RETURN_KEY_TYPES = [
	'DEFAULT', 'DONE', 'EMERGENCY_CALL', 'GO', 'GOOGLE', 'JOIN', 'NEXT', 'ROUTE',
	'SEARCH', 'SEND', 'YAHOO'
];
var AUTOCAPITALIZATION_TYPES = [
	'ALL', 'NONE', 'SENTENCES', 'WORDS'
];
var KEYBOARD_PROPERTIES = ['keyboardType', 'returnKeyType', 'autocapitalization'];

// private variables
var styleOrderCounter = 1;
var platform;

exports.setPlatform = function(p) {
	platform = p;
};

/*
 * @property {Array} globalStyle
 * The global style array, which contains an merged, ordered list of all
 * applicable global styles. This will serve as the base for all controller-
 * specific styles.
 *
 */
exports.globalStyle = [];

/*
 * @property {Object} bindingsMap
 * Holds the collection of models/collections data-bound to UI component
 * properties in the Alloy app. This map is used to create the most effecient
 * set of event listeners possible for dynamically updating the UI based on
 * changes to the data model/collections.
 *
 */
exports.bindingsMap = {};

/*
 * @method loadGlobalStyles
 * Loads all global styles (app.tss) in a project and sorts them appropriately.
 * The order of sorting is as follows:
 *
 * 1. global
 * 2. global theme
 * 3. global platform-specific
 * 4. global theme platform-specific
 *
 * This function updates the global style array that will be used as a base for
 * all controller styling. This is executed before any other styling is
 * performed during the compile phase. If the style is loaded from the cache,
 * it returns true, otherwise it returns false.
 *
 * @param {String} Full path to the "app" folder of the target project
 * @param {String} The mobile platform for which to load styles
 * @param {Object} [opts] Additional options
 *
 * @returns {Boolean} true if cache was used, false if not
 */
exports.loadGlobalStyles = function(appPath, opts) {
	// reset the global style array
	exports.globalStyle = [];

	// validate/set arguments
	opts = opts || {};
	var ret = false;
	var theme = opts.theme;
	var apptss = CONST.GLOBAL_STYLE;
	var stylesDir = path.join(appPath, CONST.DIR.STYLE);
	var themesDir;
	if (theme) {
		themesDir = path.join(appPath, 'themes', theme, CONST.DIR.STYLE);
	}
	var buildlog = BuildLog();
	var cacheFile = path.join(appPath, '..', CONST.DIR.BUILD, GLOBAL_STYLE_CACHE);

	// create array of global styles to load based on arguments
	var loadArray = [];
	loadArray.push({
		path: path.join(stylesDir, apptss),
		msg: apptss
	});
	if (theme) {
		loadArray.push({
			path: path.join(themesDir, apptss),
			msg: apptss + '(theme:' + theme + ')',
			obj: { theme: true }
		});
	}
	loadArray.push({
		path: path.join(stylesDir, platform, apptss),
		msg: apptss + '(platform:' + platform + ')',
		obj: { platform: true }
	});
	if (theme) {
		loadArray.push({
			path: path.join(themesDir, platform, apptss),
			msg: apptss + '(theme:' + theme + ' platform:' + platform + ')',
			obj: { platform: true, theme: true }
		});
	}

	// get rid of entries that don't exist
	var len = loadArray.length;
	for (var i = len - 1; i >= 0; i--) {
		if (!path.existsSync(loadArray[i].path)) {
			loadArray.splice(i, 1);
		}
	}

	// create hash of existing global styles
	var hash = U.createHash(_.pluck(loadArray, 'path'));

	// see if we can use the cached global style
	if (buildlog.data.globalStyleCacheHash === hash && fs.existsSync(cacheFile)) {
		// load global style object from cache
		logger.info('[global style] loading from cache...');
		exports.globalStyle = JSON.parse(fs.readFileSync(cacheFile, 'utf8'));
		ret = true;

		// increment the style order counter with the number of rules in the global style
		styleOrderCounter += exports.globalStyle.length;
	} else {
		// add new hash to the buildlog
		buildlog.data.globalStyleCacheHash = hash;

		// create the new global style object
		_.each(loadArray, function(g) {
			if (path.existsSync(g.path)) {
				logger.info('[' + g.msg + '] global style processing...');
				exports.globalStyle = exports.loadAndSortStyle(g.path, _.extend(
					{ existingStyle: exports.globalStyle },
					g.obj || {}
				));
			}
		});

		// write global style object to cache
		logger.info('[global style] writing to cache...');
		fs.writeFileSync(cacheFile, JSON.stringify(exports.globalStyle));

		// simply increment the style order counter
		styleOrderCounter++;
	}

	return ret;
};

/*
 * @method sortStyles
 * Given a parsed style from loadStyle(), sort all the style entries into an
 * ordered array. This is the final operations to prepare a style for usage with
 * a Titanium UI component in Alloy.
 *
 * @param {Object} Parsed style object from a loadStyle() call
 * @param {Object} [opts] Options for this function
 */
exports.sortStyles = function(style, opts) {
	var sortedStyles = [];
	opts = opts || {};

	if (_.isObject(style) && !_.isEmpty(style)) {
		for (var key in style) {
			var obj = {};
			var priority = styleOrderCounter++ * VALUES.ORDER;
			var match = key.match(STYLE_REGEX);
			if (match === null) {
				U.die('Invalid style specifier "' + key + '"');
			}
			var newKey = match[2];

			// skip any invalid style entries
			if (newKey === 'undefined' && !match[1]) { continue; }

			// get the style key type
			switch (match[1]) {
				case '#':
					obj.isId = true;
					priority += VALUES.ID;
					break;
				case '.':
					obj.isClass = true;
					priority += VALUES.CLASS;
					break;
				default:
					if (match[2]) {
						obj.isApi = true;
						priority += VALUES.API;
					}
					break;
			}

			if (match[3]) {
				obj.queries = {};
				_.each(match[3].replace(/\s*,\s*/g, ',').split(/\s+/), function(query) {
					var parts = query.split('=');
					var q = U.trim(parts[0]);
					var v = U.trim(parts[1]);
					if (q === 'platform') {
						priority += VALUES.PLATFORM + VALUES.SUM;
						v = v.split(',');
					} else if (q === 'formFactor') {
						priority += VALUES.FORMFACTOR + VALUES.SUM;
					} else if (q === 'if') {
						priority += VALUES.TSSIF + VALUES.SUM;
					} else {
						priority += VALUES.SUM;
					}
					obj.queries[q] = v;
				});
			}

			_.extend(obj, {
				priority: priority + (opts.platform ? VALUES.PLATFORM : 0) + (opts.theme ? VALUES.THEME : 0),
				key: newKey,
				style: style[key]
			});
			sortedStyles.push(obj);
		}
	}

	var theArray = opts.existingStyle ? opts.existingStyle.concat(sortedStyles) : sortedStyles;
	return _.sortBy(theArray, 'priority');
};

exports.loadStyle = function(tssFile) {
	if (path.existsSync(tssFile)) {
		// read the style file
		var contents;
		try {
			contents = fs.readFileSync(tssFile, 'utf8');
		} catch (e) {
			U.die('Failed to read style file "' + tssFile + '"', e);
		}

		// skip if the file is empty
		if (/^\s*$/gi.test(contents)) {
			return {};
		}

		// Add enclosing curly braces, if necessary
		contents = /^\s*\{[\s\S]+\}\s*$/gi.test(contents) ? contents : '{\n' + contents + '\n}';
		// [ALOY-793] double-escape '\' in tss
		contents = contents.replace(/(\s)(\\+)(\s)/g, '$1$2$2$3');

		// Process tss file then convert to JSON
		var json;
		try {
			json = grammar.parse(contents);
			optimizer.optimizeStyle(json);
		} catch (e) {
			U.die([
				'Error processing style "' + tssFile + '"',
				e.message,
				/Expected bare word\, comment\, end of line\, string or whitespace but ".+?" found\./.test(e.message) ? 'Do you have an extra comma in your style definition?' : '',
				'- line:    ' + e.line,
				'- column:  ' + e.column,
				'- offset:  ' + e.offset
			]);
		}

		return json;
	}
	return {};
};

exports.loadAndSortStyle = function(tssFile, opts) {
	return exports.sortStyles(exports.loadStyle(tssFile), opts);
};

exports.createVariableStyle = function(keyValuePairs, value) {
	var style = {};

	if (!_.isArray(keyValuePairs)) {
		keyValuePairs = [[keyValuePairs, value]];
	}
	_.each(keyValuePairs, function(pair) {
		var k = pair[0];
		var v = pair[1];
		style[k] = { value:v };
		style[k][STYLE_ALLOY_TYPE] = 'var';
	});
	return style;
};

exports.processStyle = function(_style, _state) {
	var theState = _state || {};
	var regex = EXPR_REGEX;
	var code = '';

	function processStyle(style, opts) {
		opts = opts || {};
		style = opts.fromArray ? {0:style} : style;
		var groups = {}, sn, value;

		// need to add "properties" and bindIds for ListItems
		if (theState && theState.isListItem && opts.firstOrder && !opts.fromArray) {
			for (sn in style) {
				value = style[sn];
				var prefixes = sn.split(':');
				if (prefixes.length > 1) {
					var bindId = prefixes[0];
					groups[bindId] = groups[bindId] || {};
					groups[bindId][prefixes.slice(1).join(':')] = value;
				} else {
					// allow template to be specified
					if (sn === 'template') {
						groups.template = value;
					} else {
						groups.properties = groups.properties || {};
						groups.properties[sn] = value;
					}
				}
			}
			style = groups;
		}

		for (sn in style) {
			value = style[sn];
			var prefix = opts.fromArray ? '' : sn + ':';

			if (_.isString(value)) {
				var matches = value.match(regex);
				if (matches !== null) {
					code += prefix + matches[1] + ','; // matched a JS expression
				} else {
					if (typeof style.type !== 'undefined' && typeof style.type.indexOf === 'function' && (style.type).indexOf('UI.PICKER') !== -1 && value !== 'picker') {
						if (DATEFIELDS.indexOf(sn) !== -1) {
							// ALOY-263, support date/time style pickers
							var d = U.createDate(value);

							if (U.isValidDate(d, sn)) {
								code += prefix + 'new Date("' + d.toString() + '"),';
							}
						} else {
							code += prefix + '"' + value
								.replace(/"/g, '\\"')
								.replace(/\n/g, '\\n')
								.replace(/\r/g, '\\r')
								.replace(/\u2028/g, '\\u2028')
								.replace(/\u2029/g, '\\u2029') +  '",'; // just a string
						}
					} else {
						if (KEYBOARD_PROPERTIES.indexOf(sn) === -1) {
							code += prefix + '"' + value
								.replace(/"/g, '\\"')
								.replace(/\n/g, '\\n')
								.replace(/\r/g, '\\r')
								.replace(/\u2028/g, '\\u2028')
								.replace(/\u2029/g, '\\u2029') +  '",'; // just a string
						} else {
							// keyboard type shortcuts for TextField, TextArea
							// support shortcuts for keyboard type, return key type, and autocapitalization
							if (sn === KEYBOARD_PROPERTIES[0] && _.contains(KEYBOARD_TYPES, value.toUpperCase())) {
								code += prefix + 'Ti.UI.KEYBOARD_' + value.toUpperCase() + ',';
							}
							if (sn === KEYBOARD_PROPERTIES[1] && _.contains(RETURN_KEY_TYPES, value.toUpperCase())) {
								code += prefix + 'Ti.UI.RETURNKEY_' + value.toUpperCase() + ',';
							}
							if (sn === KEYBOARD_PROPERTIES[2] && _.contains(AUTOCAPITALIZATION_TYPES, value.toUpperCase())) {
								code += prefix + 'Ti.UI.TEXT_AUTOCAPITALIZATION_' + value.toUpperCase() + ',';
							}
						}
					}
				}
			} else if (_.isArray(value)) {
				code += prefix + '[';
				_.each(value, function(v) {
					processStyle(v, {fromArray:true});
				});
				code += '],';
			} else if (_.isObject(value)) {
				if (value[STYLE_ALLOY_TYPE] === 'var') {
					code += prefix + value.value + ','; // dynamic variable value
				} else {
					// recursively process objects
					code += prefix + '{';
					processStyle(value);
					code += '},';
				}
			} else {
				code += prefix + JSON.stringify(value) + ','; // catch all, just stringify the value
			}
		}
	}
	processStyle(_style, {firstOrder:true});

	return code;
};

exports.generateStyleParams = function(styles, classes, id, apiName, extraStyle, theState) {
	var styleCollection = [],
		lastObj = {},
		elementName = apiName.split('.').pop();

	// don't add an id to the generated style if we are in a local state
	if (theState && theState.local) {
		delete extraStyle.id;
	}

	// process all style items, in order
	_.each(styles, function(style) {
		if ((style.isId && style.key === id) ||
			(style.isClass && _.contains(classes, style.key)) ||
			(style.isApi && elementName === style.key)) {

			// manage potential runtime conditions for the style
			var conditionals = {
				platform: [],
				formFactor: ''
			};

			if (style.queries) {
				// handle platform device query
				// - Make compile time comparison if possible
				// - Add runtime conditional if platform is not known
				var q = style.queries;
				if (q.platform) {
					if (platform) {
						var isForCurrentPlatform = false;
						_.each(q.platform.toString().split(','), function(p) {
							// need to account for multiple platforms and negation, such as platform=ios or
							// platform=ios,android   or   platform=!ios   or   platform="android,!mobileweb"
							if (p === platform || (p.indexOf('!') === 0 && p.substr(1) !== platform)) {
								isForCurrentPlatform = true;
							}
						});
						if (!isForCurrentPlatform) {
							return;
						}
					} else {
						_.each(q.platform, function(p) {
							conditionals.platform.push(CU.CONDITION_MAP[p]['runtime']);
						});
					}
				}

				// handle formFactor device query
				if (q.formFactor === 'tablet') {
					conditionals.formFactor = 'Alloy.isTablet';
				} else if (q.formFactor === 'handheld') {
					conditionals.formFactor = 'Alloy.isHandheld';
				}

				// assemble runtime query
				var pcond = conditionals.platform.length > 0 ? '(' + conditionals.platform.join(' || ') + ')' : '';
				var joinString = (pcond && conditionals.formFactor) ? ' && ' : '';
				var conditional = pcond + joinString + conditionals.formFactor;
				if (q.if) {
					// ALOY-871: handle custom TSS queries with if conditional
					var ffcond = conditionals.formFactor.length > 0 ? '(' + conditionals.formFactor + ')' : '';
					var ffJoinString = (ffcond) ? ' && ' : '';
					conditional = pcond + joinString + ffcond + ffJoinString + '(' + q.if.split(',').join(' || ') + ')';
				}

				// push styles if we need to insert a conditional
				if (conditional) {
					if (lastObj) {
						styleCollection.push({style:lastObj});
						styleCollection.push({style:style.style, condition:conditional});
						lastObj = {};
					}
				} else if (!q.if) {
					lastObj = deepExtend(true, lastObj, style.style);
				}
			} else {
				// remove old style
				_.each(style.style, function(val, key) {
					if (_.isArray(val) && lastObj.hasOwnProperty(key)) {
						delete lastObj[key];
					}
				});
				lastObj = deepExtend(true, lastObj, style.style);
			}
		}
	});

	// add in any final styles
	_.extend(lastObj, extraStyle || {});
	if (!_.isEmpty(lastObj)) { styleCollection.push({style:lastObj}); }

	// substitutions for binding
	_.each(styleCollection, function(style) {
		_.each(style.style, function(v, k) {

			if (!_.isString(v)) {
				return;
			}

			var bindingStrParts = v.split(BINDING_SPLIT_REGEX);

			if (bindingStrParts.length <= 1) {
				return;
			}

			var collectionModelVar = theState && theState.model ? theState.model : CONST.BIND_MODEL_VAR;

			var bindsModels = [];
			var bindsCollection = false;

			var bindingExpParts = [];

			bindingStrParts.forEach(function(part, i) {

				// empty string
				if (part === '') {
					return;
				}

				var partMatch = part.match(BINDING_REFERENCE_REGEX);

				// regular string
				if (!partMatch) {

					// escape single quote: ALOY-1478
					bindingExpParts.push("'" + part.replace(/'/g, "\\'") + "'");

					return;
				}

				var reference = partMatch[1].trim(); // trim: ALOY-716
				var referencePath = toPath(reference);

				var attribute, modelVar;

				// collection binding
				if (referencePath.length === 1) {
					bindsCollection = true;
				} else if (referencePath[0] === '$') {
					// instance model binding
					attribute = referencePath.splice(2, referencePath.length);
				} else if (_.contains(CU.models, referencePath[0])) {
					// global model binding
					attribute = referencePath.splice(1, referencePath.length);
					referencePath.unshift('Alloy', 'Models');
				} else {
					// collection binding (deep)
					bindsCollection = true;
				}

				// model binding
				if (attribute !== undefined) {
					modelVar = fromPath(referencePath);
					reference = fromPath(referencePath.concat(CONST.BIND_TRANSFORM_VAR, attribute));

					if (!_.contains(bindsModels, modelVar)) {
						bindsModels.push(modelVar);
					}
				} else {
					// collection binding
					reference = collectionModelVar + '.' + CONST.BIND_TRANSFORM_VAR + (reference[0] === '[' ? '' : '.') + reference;
				}

				bindingExpParts.push(reference);

			});

			if (!bindsCollection && bindsModels.length === 0) {
				return;
			}

			if (bindsCollection && bindsModels.length > 0) {
				U.die('Attempt to mix model (' + bindsModels.join(', ') + ') and collection (' + collectionModelVar + ') binding in: ' + v);
			}

			var bindingExp = bindingExpParts.join(' + ');

			if (bindsCollection) {
				style.style[k] = STYLE_EXPR_PREFIX + bindingExp;
			}

			if (bindsModels.length > 0) {

				bindsModels.forEach(function(modelVar) {

					// ensure that the bindings for this model have been initialized
					if (!exports.bindingsMap[modelVar]) {
						exports.bindingsMap[modelVar] = {
							models: bindsModels,
							bindings: []
						};
					} else {
						// ensure that mix use of models contains all
						exports.bindingsMap[modelVar].models = _.union(exports.bindingsMap[modelVar].models, bindsModels);
					}

					// create the binding object
					var bindingObj = {
						id: id,
						prop: k,
						val: bindingExp
					};

					// make sure bindings are wrapped in any conditionals
					// relevant to the curent style
					if (theState.condition) {
						bindingObj.condition = theState.condition;
					}

					// add this property to the global bindings map for the
					// current controller component
					exports.bindingsMap[modelVar].bindings.push(bindingObj);
				});

				// since this property is data bound, don't include it in
				// the style statically
				delete style.style[k];
			}
		});
	});

	// Let's assemble the fastest factory method object possible based on
	// what we know about the style we just sorted and assembled
	var code = '';
	if (styleCollection.length === 0) {
		code += '{}';
	} else if (styleCollection.length === 1) {
		if (styleCollection[0].condition) {
			// check the condition and return the object
			code += styleCollection[0].condition + ' ? {' + exports.processStyle(styleCollection[0].style, theState) + '} : {}';
		} else {
			// just return the object
			code += '{';
			code += exports.processStyle(styleCollection[0].style, theState);
			code += '}';
		}
	} else if (styleCollection.length > 1) {
		// construct self-executing function to merge styles based on runtime conditionals
		code += '(function(){\n';
		code += 'var o = {};\n';

		for (var i = 0, l = styleCollection.length; i < l; i++) {
			var tmpStyle = exports.processStyle(styleCollection[i].style, theState);
			if (!_.isEmpty(tmpStyle)) {
				if (styleCollection[i].condition) {
					code += 'if (' + styleCollection[i].condition + ') ';
				}
				code += 'Alloy.deepExtend(true, o, {';
				code += tmpStyle;
				code += '});\n';
			}
		}
		code += 'return o;\n';
		code += '})()';
	}

	return code;
};

function getCacheFilePath(appPath, hash) {
	return path.join(appPath, '..', CONST.DIR.BUILD, 'global_style_cache_' + hash + '.json');
}

// source: https://github.com/lodash/lodash/blob/3.8.1-npm-packages/lodash._topath/index.js
function toPath(value) {
	var result = [];
	value.replace(/[^.[\]]+|\[(?:(-?\d+(?:\.\d+)?)|(["'])((?:(?!\2)[^\n\\]|\\.)*?)\2)\]/g, function(match, number, quote, string) {
		result.push(quote ? string.replace(/\\(\\)?/g, '$1') : (number || match));
	});
	return result;
}

function fromPath(path) {
	var result = path[0];

	if (path.length > 1) {
		result += '[' + path.slice(1).map(function(string) {
			return "'" + string.replace(/'/g, "\\'") + "'";
		}).join('][') + ']';
	}

	return result;
}

var U = require('../../utils'),
	colors = require('colors'),
	path = require('path'),
	os = require('os'),
	fs = require('fs'),
	wrench = require('wrench'),
	jsonlint = require('jsonlint'),
	logger = require('../../logger'),
	astController = require('./ast/controller'),
	_ = require('../../lib/alloy/underscore')._,
	styler = require('./styler'),
	XMLSerializer = require('xmldom').XMLSerializer,
	CONST = require('../../common/constants');

///////////////////////////////////////
////////// private variables //////////
///////////////////////////////////////
var alloyRoot = path.join(__dirname, '..', '..'),
	platformsDir = path.join(alloyRoot, '..', 'platforms'),
	alloyUniqueIdPrefix = '__alloyId',
	alloyUniqueIdCounter = 0,
	JSON_NULL = JSON.parse('null'),
	compilerConfig;

///////////////////////////////
////////// constants //////////
///////////////////////////////
var RESERVED_ATTRIBUTES = [
		'platform',
		'formFactor',
		'if',
		CONST.BIND_COLLECTION,
		CONST.BIND_WHERE,
		CONST.AUTOSTYLE_PROPERTY,
		'ns',
		'method',
		'module'
	],
	RESERVED_ATTRIBUTES_REQ_INC = [
		'platform',
		'type',
		'src',
		'formFactor',
		'if',
		CONST.BIND_COLLECTION,
		CONST.BIND_WHERE,
		CONST.AUTOSTYLE_PROPERTY,
		'ns',
		'method',
		'module'
	],
	RESERVED_EVENT_REGEX =  /^on([A-Z].+)/;

// load CONDITION_MAP with platforms
exports.CONDITION_MAP = {
	handheld: {
		runtime: '!Alloy.isTablet'
	},
	tablet: {
		runtime: 'Alloy.isTablet'
	}
};
_.each(CONST.PLATFORMS, function(p) {
	exports.CONDITION_MAP[p] = require(path.join(platformsDir, p, 'index'))['condition'];
});

exports.bindingsMap = {};
exports.destroyCode = '';
exports.postCode = '';
exports.models = [];
exports.dataFunctionNames = {};

//////////////////////////////////////
////////// public interface //////////
//////////////////////////////////////
exports.getCompilerConfig = function() {
	return compilerConfig;
};

exports.generateVarName = function(id, name) {
	if (_.contains(CONST.JS_RESERVED_ALL, id)) {
		U.die([
			'Invalid ID "' + id + '" for <' + name + '>.',
			'Can\'t use reserved Javascript words as IDs.',
			'Reserved words: [' + CONST.JS_RESERVED_ALL.sort().join(',') + ']'
		]);
	}
	return '$.__views.' + id;
};

exports.generateUniqueId = function() {
	return alloyUniqueIdPrefix + alloyUniqueIdCounter++;
};

exports.getNodeFullname = function(node) {
	var name = node.nodeName,
		ns = node.getAttribute('ns') || CONST.IMPLICIT_NAMESPACES[name] || CONST.NAMESPACE_DEFAULT,
		fullname = ns + '.' + name;

	return fullname;
};

exports.isNodeForCurrentPlatform = function(node) {
	var isForCurrentPlatform =  !node.hasAttribute('platform') || !compilerConfig || !compilerConfig.alloyConfig;
	_.each(node.getAttribute('platform').split(','), function(p) {
		// need to account for multiple platforms and negation, such as
		// platform=ios,android   or   platform=!ios   or   platform="android,!mobileweb"
		p = p.trim();
		if (p === compilerConfig.alloyConfig.platform || (p.indexOf('!') === 0 && p.slice(1) !== compilerConfig.alloyConfig.platform)) {
			isForCurrentPlatform = true;
		}
	});
	return isForCurrentPlatform;
};
exports.getParserArgs = function(node, state, opts) {
	state = state || {};
	opts = opts || {};

	var defaultId = opts.defaultId || undefined,
		doSetId = opts.doSetId === false ? false : true,
		name = node.nodeName,
		ns = node.getAttribute('ns') || CONST.IMPLICIT_NAMESPACES[name] || CONST.NAMESPACE_DEFAULT,
		fullname = ns + '.' + name,
		id = node.getAttribute('id') || defaultId || exports.generateUniqueId(),
		platform = node.getAttribute('platform'),
		formFactor = node.getAttribute('formFactor'),
		tssIf = node.getAttribute('if'),
		platformObj;

	// make sure we're not reusing the default ID for the first top level element
	if (id === exports.currentDefaultId &&
		(node.parentNode && node.parentNode.nodeName !== 'Alloy') &&
		!node.__idWarningHandled) {
		logger.warn([
			'<' + name + '> at line ' + node.lineNumber +
			' is using this view\'s default ID "' + id + '". ' +
			'Only a top-level element in a view should use the default ID'
		]);
		node.__idWarningHandled = true;
	}

	// handle binding arguments
	var bindObj = {};
	bindObj[CONST.BIND_COLLECTION] = node.getAttribute(CONST.BIND_COLLECTION);
	bindObj[CONST.BIND_WHERE] = node.getAttribute(CONST.BIND_WHERE);
	bindObj[CONST.BIND_TRANSFORM] = node.getAttribute(CONST.BIND_TRANSFORM);
	bindObj[CONST.BIND_FUNCTION] = node.getAttribute(CONST.BIND_FUNCTION);

	// cleanup namespaces and nodes
	ns = ns.replace(/^Titanium\./, 'Ti.');
	if (doSetId && !_.contains(CONST.MODEL_ELEMENTS, fullname)) {
		node.setAttribute('id', id);
	}

	// process the platform attribute
	if (platform) {
		platformObj = {};
		_.each((platform).split(','), function(p) {
			var matches = U.trim(p).match(/^(\!{0,1})(.+)/);
			if (matches !== null) {
				var negate = matches[1];
				var name = matches[2];
				if (_.contains(CONST.PLATFORMS, name)) {
					if (negate === '!') {
						_.each(_.without(CONST.PLATFORMS, name), function(n) {
							platformObj[n] = true;
						});
					} else {
						platformObj[name] = true;
					}
					return;
				}
			}
			U.die('Invalid platform type found: ' + p);
		});
	}

	// get create arguments and events from attributes
	var createArgs = {},
		events = [];
	var attrs = _.contains(['Alloy.Require'], fullname) ?
		RESERVED_ATTRIBUTES_REQ_INC :
		RESERVED_ATTRIBUTES;

	// determine whether to autoStyle this component
	// 1. autoStyle attribute
	// 2. autoStyle from <Alloy>
	// 3. autoStyle from config.json
	var autoStyle = (function() {
		var prop = CONST.AUTOSTYLE_PROPERTY;
		if (node.hasAttribute(prop)) {
			return node.getAttribute(prop) === 'true';
		} else {
			return exports[prop];
		}
	})();

	// TODO: Add the apiName until TIMOB-12553 is resolved
	if (autoStyle) {
		createArgs[CONST.APINAME_PROPERTY] = fullname;
	}

	_.each(node.attributes, function(attr) {
		var attrName = attr.nodeName;
		if (_.contains(attrs, attrName)) { return; }
		var matches = attrName.match(RESERVED_EVENT_REGEX);
		if (matches !== null && exports.isNodeForCurrentPlatform(node) && !_.contains(CONST.SPECIAL_PROPERTY_NAMES, attrName)) {
			events.push({
				name: U.lcfirst(matches[1]),
				value: node.getAttribute(attrName)
			});
		} else {
			var theValue = node.getAttribute(attrName);
			if (/(^|\+)\s*(?:(?:Ti|Titanium|Alloy.Globals|Alloy.CFG)\.|L\(.+\)\s*$)/.test(theValue)) {
				var match = theValue.match(/^\s*L\([^'"]+\)\s*$/);
				if (match !== null) {
					theValue = theValue.replace(/\(/g, '("').replace(/\)/g, '")');
				}
				theValue = styler.STYLE_EXPR_PREFIX + theValue;
			}


			if (attrName === 'class') {
				if (autoStyle) {
					createArgs[CONST.CLASS_PROPERTY] = theValue.split(/\s+/) || [];
				}
			} else {
				if (theValue === 'true') {
					theValue = true;
				} else if (theValue === 'false') {
					theValue = false;
				} else {
					var n = parseInt(theValue);
					if (!isNaN(n) && String(n) === theValue.trim()) {
						theValue = n;
					} else {
						n = parseFloat(theValue);
						if (!isNaN(n) && String(n) === theValue.trim()) {
							theValue = n;
						}
					}
				}
				createArgs[attrName] = theValue;
			}
		}
	});

	if (autoStyle && !createArgs[CONST.CLASS_PROPERTY]) {
		createArgs[CONST.CLASS_PROPERTY] = [];
	}

	return _.extend({
		ns: ns,
		name: name,
		id: id,
		fullname: fullname,
		formFactor: node.getAttribute('formFactor'),
		symbol: exports.generateVarName(id, name),
		classes: node.getAttribute('class').split(' ') || [],
		tssIf: node.getAttribute('if').split(',') || [],
		parent: state.parent || {},
		platform: platformObj,
		createArgs: createArgs,
		events: events
	}, bindObj);
};

exports.generateNodeExtended = function(node, state, newState) {
	return exports.generateNode(node, _.extend(_.clone(state), newState));
};

exports.generateNode = function(node, state, defaultId, isTopLevel, isModelOrCollection) {
	if (node.nodeType != 1) return '';
	if (!exports.isNodeForCurrentPlatform(node)) {
		return '';
	}

	var args = exports.getParserArgs(node, state, { defaultId: defaultId }),
		codeTemplate = 'if (<%= condition %>) {\n<%= content %>}\n',
		code = {
			content: '',
			pre: ''
		};

	// Check for platform specific considerations
	var conditionType = compilerConfig && compilerConfig.alloyConfig && compilerConfig.alloyConfig.platform ? 'compile' : 'runtime';
	if (args.platform) {
		var conditionArray = [];
		_.each(args.platform, function(v, k) {
			conditionArray.push(exports.CONDITION_MAP[k][conditionType]);
		});

		code.condition = '(' + conditionArray.join(' || ') + ')';
	}

	//Add form factor condition, if application form-factor specific runtime check
	if (args.formFactor && exports.CONDITION_MAP[args.formFactor]) {
		var check = exports.CONDITION_MAP[args.formFactor].runtime;
		code.condition = (code.condition) ? code.condition += ' && ' + check : check;
	}

	// ALOY-871: add the if condition check
	args.tssIf = _.compact(args.tssIf);
	if (args.tssIf.length > 0) {
		if (code.condition) {
			code.condition += (' && (' + args.tssIf.join(' || ') + ')');
		} else {
			code.condition = args.tssIf.join(' || ');
		}
	}

	// pass relevant conditional information in state
	if (code.condition) {
		if (state.condition) {
			state.condition += '&&' + code.condition;
		} else {
			state.condition = code.condition;
		}
	}

	// Determine which parser to use for this node
	var parsersDir = path.join(alloyRoot, 'commands', 'compile', 'parsers');
	var parserRequire = 'default';
	if (_.contains(fs.readdirSync(parsersDir), args.fullname + '.js')) {
		parserRequire = args.fullname + '.js';
	}

	// Execute the appropriate tag parser and append code
	var isLocal = state.local;
	// [ALOY-787] keeping track of widget id
	var widgetId = state.widgetId;
	state = require('./parsers/' + parserRequire).parse(node, state) || { parent: {} };
	code.content += state.code;
	state.widgetId = widgetId;

	// Use local variable if given
	if (isLocal && state.parent) { args.symbol = state.parent.symbol || args.symbol; }

	// Use manually given args.symbol if present
	if (state.args) { args.symbol = state.args.symbol || args.symbol; }

	// add to list of top level views, if its top level
	if (isTopLevel) {
		if (state.isProxyProperty) {
			delete state.isProxyProperty;
			code.content += state.parent.symbol + ' && $.addProxyProperty("' + state.propertyName +
				'", ' + state.parent.symbol + ');';
		} else {
			code.content += args.symbol + ' && $.addTopLevelView(' + args.symbol + ');';
		}
	}

	// handle any model/collection code
	if (state.modelCode) {
		code.pre += state.modelCode;
		delete state.modelCode;
	}

	// handle any events from markup
	if (args.events && args.events.length > 0 &&
		!_.contains(CONST.SKIP_EVENT_HANDLING, args.fullname) &&
		!state.isViewTemplate) {
		// determine which function name to use for event handling:
		// * addEventListener() for Titanium proxies
		// * on() for everything else (controllers, models, collections)
		var eventFunc = /^Alloy\.(?:Collection|Model|Require|Widget)/.test(args.fullname) ?
			'on' : 'addEventListener';

		_.each(args.events, function(ev) {
			var eventObj = {
					obj: isModelOrCollection ? state.args.symbol : args.symbol,
					ev: ev.name,
					cb: ev.value,
					escapedCb: ev.value.replace(/'/g, "\\'"),
					func: eventFunc
				},
				postCode;

			if (args.fullname === 'Alloy.Widget') {
				eventObj.obj = state.controller;
			}

			// create templates for immediate and deferred event handler creation
			var theDefer = _.template("__defers['<%= obj %>!<%= ev %>!<%= escapedCb %>']", eventObj);
			var theEvent;
			if (eventFunc === 'addEventListener') {
				theEvent = _.template("$.addListener(<%= obj %>,'<%= ev %>',<%= cb %>)", eventObj);
			} else {
				theEvent = _.template("<%= obj %>.<%= func %>('<%= ev %>',<%= cb %>)", eventObj);
			}
			var deferTemplate = theDefer + ' && ' + theEvent + ';';
			var immediateTemplate;
			if (/[\.\[]/.test(eventObj.cb)) {
				immediateTemplate = 'try{' + theEvent + ';}catch(e){' + theDefer + '=true;}';
			} else {
				immediateTemplate = '<%= cb %>?' + theEvent + ':' + theDefer + '=true;';
			}

			// add the generated code to the view code and post-controller code respectively
			code.content += _.template(immediateTemplate, eventObj);
			postCode = _.template(deferTemplate, eventObj);
			exports.postCode += state.condition ? _.template(codeTemplate, {
				condition: state.condition,
				content: postCode
			}) : postCode;
		});
	}

	// Continue parsing if necessary
	if (state.parent) {
		var states = _.isArray(state.parent) ? state.parent : [state.parent];
		_.each(states, function(p) {
			var parent = p.node;
			if (!parent) { return; }
			for (var i = 0, l = parent.childNodes.length; i < l; i++) {
				var newState = _.defaults({ parent: p }, state);
				if (node.hasAttribute('formFactor') || state.parentFormFactor) {
					// propagate the form factor down through the hierarchy
					newState.parentFormFactor = (node.getAttribute('formFactor') || state.parentFormFactor);
				}
				code.content += exports.generateNode(parent.childNodes.item(i), newState);
			}
		});
	}

	if (!isModelOrCollection) {
		return code.condition ? _.template(codeTemplate, code) : code.content;
	} else {
		return {
			content: code.condition ? _.template(codeTemplate, code) : code.content,
			pre: code.condition ? _.template(codeTemplate, {content:code.pre}) : code.pre
		};
	}
};

exports.expandRequireNode = function(requireNode, doRecursive) {
	var cloneNode = requireNode.cloneNode(true);

	function getViewRequirePath(node) {
		var regex = new RegExp('\\.' + CONST.FILE_EXT.VIEW + '$'),
			src = node.getAttribute('src'),
			fullname = exports.getNodeFullname(node),
			name = node.getAttribute('name') || CONST.NAME_WIDGET_DEFAULT,
			type = fullname === 'Alloy.Widget' ? 'widget' : node.getAttribute('type') || CONST.REQUIRE_TYPE_DEFAULT,
			fullpaths = [];

		var platform;
		if (compilerConfig && compilerConfig.alloyConfig && compilerConfig.alloyConfig.platform) {
			platform = compilerConfig.alloyConfig.platform;
		}

		// Must be a view, with a valid src, in a <Require> element
		if (!src) {
			return null;
		} else if (fullname === 'Alloy.Require' && type === 'view') {
			if (platform) { fullpaths.push(path.join(compilerConfig.dir.views, platform, src)); }
			fullpaths.push(path.join(compilerConfig.dir.views, src));
		} else if (fullname === 'Alloy.Widget' ||
			(fullname === 'Alloy.Require' && type === 'widget')) {
			U.getWidgetDirectories(compilerConfig.dir.home).forEach(function(wDir) {
				if (wDir.manifest.id === src) {
					if (platform) {
						fullpaths.push(path.join(wDir.dir, CONST.DIR.VIEW, platform, name));
					}
					fullpaths.push(path.join(wDir.dir, CONST.DIR.VIEW, name));
				}
			});
		} else {
			return null;
		}

		// check the extensions on the paths to check
		var found = false;
		var fullpath;
		for (var i = 0; i < fullpaths.length; i++) {
			fullpath = fullpaths[i];
			fullpath += regex.test(fullpath) ? '' :  '.' + CONST.FILE_EXT.VIEW;
			if (fs.existsSync(fullpath)) {
				found = true;
				break;
			}
		}

		// abort if there's no view to be found
		if (!found) {
			U.die([
				type + ' "' + src + '" ' + (type === 'widget' ? 'view "' + name + '" ' : '') +
					'does not exist.',
				'The following paths were inspected:'
			].concat(fullpaths));
		}

		return fullpath;
	}

	//create function, it expects 2 values.
	function insertAfter(newElement, targetElement) {
		//target is what you want it to go after. Look for this elements parent.
		var parent = targetElement.parentNode;

		//if the parents lastchild is the targetElement...
		if (parent.lastchild == targetElement) {
			//add the newElement after the target element.
			parent.appendChild(newElement);
		} else {
			// else the target has siblings, insert the new element between the target and it's next sibling.
			parent.insertBefore(newElement, targetElement.nextSibling);
		}
	}

	function processRequire(node, isFirst) {
		// make sure we have a valid required view and get its path
		var fullpath = getViewRequirePath(node);
		if (fullpath === null) {
			return;
		}

		// re-assemble XML with required elements
		if (isFirst) {
			cloneNode = U.XML.getAlloyFromFile(fullpath);
		} else {
			var newDocRoot = U.XML.getAlloyFromFile(fullpath);
			_.each(U.XML.getElementsFromNodes(newDocRoot.childNodes), function(n) {
				insertAfter(n, node);
			});

			node.parentNode.removeChild(node);
		}
	}

	// Expand the <Require>, recursively if specified
	if (getViewRequirePath(cloneNode) !== null) {
		processRequire(cloneNode, true);
		while (doRecursive) {
			var reqs = cloneNode.getElementsByTagName('Require');
			var widgets = cloneNode.getElementsByTagName('Widget');
			var all = [];

			// condense node lists into a single array
			_.each(reqs, function(req) {
				all.push(req);
			});
			_.each(widgets, function(widget) {
				all.push(widget);
			});

			// find all the valid widgets/requires
			var viewRequires = _.filter(reqs, function(req) {
				return getViewRequirePath(req) !== null;
			});

			if (viewRequires.length === 0) {
				break;
			}

			// TODO: https://jira.appcelerator.org/browse/ALOY-256
			//_.each(viewRequires, processRequire);
			processRequire(viewRequires[0]);
		}
	}

	return cloneNode;
};

exports.inspectRequireNode = function(node) {
	var newNode = exports.expandRequireNode(node, true);
	var children = U.XML.getElementsFromNodes(newNode.childNodes);
	var names = [];

	_.each(children, function(c) {
		var args = exports.getParserArgs(c);

		// skip model elements when inspecting nodes for <Require>
		if (_.contains(CONST.MODEL_ELEMENTS, args.fullname)) {
			newNode.removeChild(c);
			return;
		}

		names.push(args.fullname);
	});

	return {
		children: U.XML.getElementsFromNodes(newNode.childNodes),
		length: names.length,
		names: names
	};
};

exports.copyWidgetResources = function(resources, resourceDir, widgetId, opts) {

	opts = opts || {};
	var platform;
	if (compilerConfig && compilerConfig.alloyConfig && compilerConfig.alloyConfig.platform) {
		platform = compilerConfig.alloyConfig.platform;
	}

	_.each(resources, function(dir) {
		if (!path.existsSync(dir)) { return; }
		logger.trace('WIDGET_SRC=' + path.relative(compilerConfig.dir.project, dir));
		var files = wrench.readdirSyncRecursive(dir);
		_.each(files, function(file) {
			var source = path.join(dir, file);

			// make sure the file exists and that it is not filtered
			if (!fs.existsSync(source) ||
				(opts.filter && opts.filter.test(file)) ||
				(opts.exceptions && _.contains(opts.exceptions, file))) {
				return;
			}

			if (fs.statSync(source).isFile()) {
				var dirname = path.dirname(file);
				var parts = dirname.split(/[\/\\]/);
				if (opts.titaniumFolder && parts[0] === opts.titaniumFolder) {
					dirname = parts.slice(1).join('/');
				}

				var destDir = path.join(resourceDir, dirname, widgetId);
				var dest = path.join(destDir, path.basename(file));
				if (!path.existsSync(destDir)) {
					wrench.mkdirSyncRecursive(destDir, 0755);
				}

				logger.trace('Copying ' + file.yellow + ' --> ' +
					path.relative(compilerConfig.dir.project, dest).yellow + '...');
				U.copyFileSync(source, dest);
			}
		});

		// [ALOY-1002] Remove ios folder copied from widget
		var iosDir = path.join(resourceDir, 'ios');
		if (fs.existsSync(iosDir)) {
			wrench.rmdirSyncRecursive(iosDir);
		}
		logger.trace(' ');
	});

	if (opts.theme) {
		// if this widget has been themed, copy its theme assets atop the stock ones
		var widgetThemeDir = path.join(compilerConfig.dir.project, 'app', 'themes', opts.theme, 'widgets', widgetId);
		if (fs.existsSync(widgetThemeDir)) {
			logger.trace('Processing themed widgets');
			var widgetAssetSourceDir = path.join(widgetThemeDir, 'assets');
			var widgetAssetTargetDir = path.join(resourceDir, widgetId);
			if (fs.existsSync(widgetAssetSourceDir)) {
				wrench.copyDirSyncRecursive(widgetAssetSourceDir, widgetAssetTargetDir, {preserve: true});
			}
			// platform-specific assets from the widget must override those of the theme
			if (platform && path.existsSync(path.join(resources[0], platform))) {
				wrench.copyDirSyncRecursive(path.join(resources[0], platform), widgetAssetTargetDir, {preserve: true});
			}
			// however platform-specific theme assets must override the platform assets from the widget
			if (platform && path.existsSync(path.join(widgetAssetSourceDir, platform))) {
				logger.trace('Processing platform-specific theme assets for the ' + widgetId + ' widget');
				widgetAssetSourceDir = path.join(widgetAssetSourceDir, platform);
				wrench.copyDirSyncRecursive(widgetAssetSourceDir, widgetAssetTargetDir, {preserve: true});
			}

			// [ALOY-1002] Remove platform-specific folders copied from theme
			if (fs.existsSync(widgetAssetTargetDir)) {
				var files = wrench.readdirSyncRecursive(widgetAssetTargetDir);
				_.each(files, function(file) {
					var source = path.join(widgetAssetTargetDir, file);
					if (path.existsSync(source) && fs.statSync(source).isDirectory()) {
						wrench.rmdirSyncRecursive(source);
					}
				});
			}
		}
	}
};

exports.mergeI18N = function mergeI18N(src, dest, opts) {
	var serializer = new XMLSerializer();
	opts || (opts = {});

	(function walk(src, dest) {
		if (!fs.existsSync(src)) return;

		fs.readdirSync(src).forEach(function (name) {
			var srcFile = path.join(src, name);
			var destFile = path.join(dest, name);

			if (!fs.existsSync(srcFile)) return;

			if (fs.statSync(srcFile).isDirectory()) {
				fs.existsSync(destFile) || wrench.mkdirSyncRecursive(destFile, 0755);
				return walk(srcFile, destFile);
			}

			if (!fs.existsSync(destFile)) {
				logger.debug('Writing ' + destFile.yellow);
				return U.copyFileSync(srcFile, destFile);
			}

			if (!/\.xml$/.test(srcFile)) {
				return;
			}

			// merge!
			var existing = {};
			var destXml = U.XML.parseFromFile(destFile);
			var destDoc = destXml.documentElement;
			var srcXml = U.XML.parseFromFile(srcFile);
			var srcDoc = srcXml.documentElement;

			if (!destDoc) {
				U.die('Error processing "' + destFile + '"');
			}

			if (!srcDoc) {
				U.die('Error processing "' + srcFile + '"');
			}

			_.each(destDoc.getElementsByTagName('string'), function (node) {
				var name = node.getAttribute('name');
				existing[name] = node;
			});

			_.each(srcDoc.getElementsByTagName('string'), function (node) {
				var name = node.getAttribute('name');
				if (!existing.hasOwnProperty(name)) {
					destDoc.appendChild(destXml.createTextNode('\t'));
					destDoc.appendChild(node);
					destDoc.appendChild(destXml.createTextNode('\n'));
				} else if (opts.override) {
					destDoc.replaceChild(node, existing[name]);
				}
			});

			logger.debug('Merging ' + srcFile.yellow + ' --> ' + destFile.yellow);
			fs.writeFileSync(destFile, serializer.serializeToString(destXml), 'utf8');
		});
	}(src, dest));
};

