exports.createCompileConfig = function(inputPath, outputPath, alloyConfig, buildLog) {
	var dirs = ['assets', 'config', 'controllers', 'lib', 'migrations', 'models', 'styles', 'themes', 'vendor', 'views', 'widgets'];
	var libDirs = ['builtins', 'template'];
	var resources = path.resolve(path.join(outputPath, 'Resources'));

	var obj = {
		alloyConfig: alloyConfig,
		dir: {
			home: path.resolve(inputPath),
			project: path.resolve(outputPath),
			resources: resources,
			resourcesAlloy: path.join(resources, 'alloy')
		},
		buildLog: buildLog
	};

	// create list of dirs
	_.each(dirs, function(dir) {
		obj.dir[dir] = path.resolve(path.join(inputPath, dir));
	});
	_.each(libDirs, function(dir) {
		obj.dir[dir] = path.resolve(path.join(alloyRoot, dir));
	});

	// ensure the generated directories exist
	U.ensureDir(obj.dir.resources);

	// process and normalize the config.json file
	var configs = _.defaults(generateConfig(obj), {
		// sets the theme
		theme: undefined,

		// are we going to generate sourcemaps?
		sourcemap: true,

		// are we enabling dynamic styling for all generated components?
		autoStyle: false,

		// the list of widget dependencies
		dependencies: {},

		// TODO: Include no adapters by default
		adapters: CONST.ADAPTERS
	});

	// normalize adapters
	if (!configs.adapters) {
		configs.adapters = [];
	} else if (!_.isArray(configs.adapters)) {
		configs.adapters = [configs.adapters];
	}

	logger.debug(JSON.stringify(configs, null, '  ').split(os.EOL));

	// update implicit namespaces, if possible
	updateImplicitNamspaces(alloyConfig.platform);

	// keep a copy of the config for this module
	compilerConfig = _.extend(obj, configs);

	return obj;
};

function generateConfig(obj) {
	var buildLog = obj.buildLog;
	var o = {};
	var alloyConfig = obj.alloyConfig;
	var platform = require('../../../platforms/' + alloyConfig.platform + '/index').titaniumFolder;
	//var defaultCfg = 'module.exports=' + JSON.stringify(o) + ';';

	// get the app and resources locations
	var appCfg = path.join(obj.dir.home, 'config.' + CONST.FILE_EXT.CONFIG);
	var resourcesBase = (function() {
		var base = obj.dir.resources;
		if (platform) { base = path.join(base, platform); }
		return path.join(base, 'alloy');
	})();
	var resourcesCfg = path.join(resourcesBase, 'CFG.js');

	// parse config.json, if it exists
	if (path.existsSync(appCfg)) {
		o = exports.parseConfig(appCfg, alloyConfig, o);

		if (o.theme) {
			var themeCfg = path.join(obj.dir.home, 'themes', o.theme, 'config.' + CONST.FILE_EXT.CONFIG);

			// parse theme config.json, if it exists
			if (path.existsSync(themeCfg)) {
				o = exports.parseConfig(themeCfg, alloyConfig, o);
			}
		}
	}

	// only regenerate the CFG.js when necessary
	var hash = U.createHashFromString(JSON.stringify(o));
	if (buildLog.data.cfgHash && buildLog.data.cfgHash === hash && fs.existsSync(path.join(obj.dir.resources, 'alloy', 'CFG.js'))) {
		// use cached CFG.js file
		logger.info(' [config.json] config.json unchanged, using cached config.json...');
	} else {
		// cached CFG.js is out of sync with config.json, regenerate and save
		logger.info(' [config.json] regenerating CFG.js from config.json...');
		buildLog.data.cfgHash = hash;
		// write out the config runtime module
		wrench.mkdirSyncRecursive(resourcesBase, 0755);

		//logger.debug('Writing "Resources/' + (platform ? platform + '/' : '') + 'alloy/CFG.js"...');
		var output = 'module.exports=' + JSON.stringify(o) + ';';
		fs.writeFileSync(resourcesCfg, output);

		// TODO: deal with TIMOB-14884
		var baseFolder = path.join(obj.dir.resources, 'alloy');
		if (!fs.existsSync(baseFolder)) {
			wrench.mkdirSyncRecursive(baseFolder, 0755);
		}
		fs.writeFileSync(path.join(baseFolder, 'CFG.js'), output);
	}

	return o;
}

exports.parseConfig = function(file, alloyConfig, o) {
	var j, distType;
	try {
		j = jsonlint.parse(fs.readFileSync(file, 'utf8'));
	} catch (e) {
		U.die('Error processing "config.' + CONST.FILE_EXT.CONFIG + '"', e);
	}

	_.each(j, function(v, k) {
		if (!/^(?:env\:|os\:|dist\:)/.test(k) && k !== 'global') {
			logger.debug(k + ' = ' + JSON.stringify(v));
			o[k] = v;
		}
	});

	if (alloyConfig) {
		o = _.extend(o, j['global']);
		o = _.extend(o, j['env:' + alloyConfig.deploytype]);
		o = _.extend(o, j['os:' + alloyConfig.platform]);
		o = _.extend(o, j['env:' + alloyConfig.deploytype + ' os:' + alloyConfig.platform]);
		o = _.extend(o, j['os:' + alloyConfig.platform + ' env:' + alloyConfig.deploytype]);

		if (alloyConfig.deploytype === 'production' && alloyConfig.target) {
			distType = _.find(CONST.DIST_TYPES, function (obj) { return obj.value.indexOf(alloyConfig.target) !== -1; });
			if (distType) {
				distType = distType.key.toLowerCase().replace('_', ':');
				o = _.extend(o, j[distType]);
				o = _.extend(o, j['os:' + alloyConfig.platform + ' ' + distType]);
			}
		}
	}

	return o;
};

exports.loadController = function(file) {
	var code = {
			parentControllerName: '',
			controller: '',
			pre: ''
		},
		contents;

	// Read the controller file
	try {
		if (!path.existsSync(file)) {
			return code;
		}
		contents = fs.readFileSync(file, 'utf8');
	} catch (e) {
		U.die('Error reading controller file "' + file + '".', e);
	}

	// get the base controller for this controller
	code.controller = contents;
	code.parentControllerName = astController.getBaseController(contents, file);

	return code;
};

exports.validateNodeName = function(node, names) {
	var fullname = exports.getNodeFullname(node);
	var ret = null;
	if (!_.isArray(names)) { names = [names]; }

	// Is the node name in the given list of valid names?
	ret = _.find(names, function(name) { return name === fullname; });
	if (ret) { return ret; }

	// Is it an Alloy.Require?
	if (fullname === 'Alloy.Require' || fullname === 'Alloy.Widget') {
		var inspect = exports.inspectRequireNode(node);
		ret = _.find(inspect.children, function(n) {
			return _.contains(names, exports.getNodeFullname(n));
		});
		if (ret) {
			return exports.getNodeFullname(ret);
		}
	}

	return null;
};

exports.generateCollectionBindingTemplate = function(args) {
	var code = '';

	// Determine the collection variable to use
	var obj = { name: args[CONST.BIND_COLLECTION] };
	var col = _.template((exports.currentManifest ? CONST.WIDGET_OBJECT : 'Alloy') + ".Collections['<%= name %>'] || <%= name %>", obj);
	var colVar = exports.generateUniqueId();

	// Create the code for the filter and transform functions
	var where = args[CONST.BIND_WHERE];
	var transform = args[CONST.BIND_TRANSFORM];
	var whereCode = where ? where + '(' + colVar + ')' : colVar + '.models';
	var transformCode = transform ? transform + '(<%= localModel %>)' : '_.isFunction(<%= localModel %>.transform)?<%= localModel %>.transform():<%= localModel %>.toJSON()';
	var handlerFunc = args[CONST.BIND_FUNCTION] || exports.generateUniqueId();
	if (args.parentFormFactor) {
		if (!exports.dataFunctionNames[handlerFunc]) {
			exports.dataFunctionNames[handlerFunc] = [];
		}
		exports.dataFunctionNames[handlerFunc].push(args.parentFormFactor);
		// append the form factor for the code below
		handlerFunc += U.ucfirst(args.parentFormFactor);
	}
	// construct code template
	code += 'var ' + colVar + '=' + col + ';';
	code += 'function ' + handlerFunc + '(e) {';
	code += '   if (e && e.fromAdapter) { return; }';
	code += '   var opts = ' + handlerFunc + '.opts || {};';
	code += '	var models = ' + whereCode + ';';
	code += '	var len = models.length;';
	code += '<%= pre %>';
	code += '	for (var i = 0; i < len; i++) {';
	code += '		var <%= localModel %> = models[i];';
	if (!args.isDataBoundMap) {
		code += '		<%= localModel %>.' + CONST.BIND_TRANSFORM_VAR + ' = ' + transformCode + ';';
	} else {
		// because (ti.map).annotations[] doesn't accept an array of anonymous objects
		// we convert them to actual Annotations before pushing them to the array
		code += "		<%= annotationArray %>.push(require('ti.map').createAnnotation(" + transformCode + '));';
	}
	code += '<%= items %>';
	code += '	}';
	code += '<%= post %>';
	code += '};';
	code += colVar + ".on('" + CONST.COLLECTION_BINDING_EVENTS + "'," + handlerFunc + ');';

	exports.destroyCode += colVar + ' && ' + ((args.parentFormFactor) ? 'Alloy.is' + U.ucfirst(args.parentFormFactor) + ' && ' : '' ) +
		colVar + ".off('" + CONST.COLLECTION_BINDING_EVENTS + "'," + handlerFunc + ');';

	return code;
};

var U = require('../../../utils'),
	uglifyjs = require('uglify-js');

exports.getBaseController = function(code, file) {
	var baseController = '';

	try {
		var ast = uglifyjs.parse(code);
		ast.walk(new uglifyjs.TreeWalker(function(node) {
			if (node instanceof uglifyjs.AST_Assign) {
				var left = node.left.print_to_string();
				if (left === 'exports.baseController' ||
					left === 'exports["baseController"]' ||
					left === "exports['baseController']") {
					baseController = node.right.print_to_string();
				}
			}
		}));
	} catch (e) {
		U.die('Error generating AST for "' + file + '"', e);
	}

	return baseController;
};

/**
 * Code in this file will attempt to optimize generated code so it's more compact
 * and executes faster, better, etc.
 */
var uglifyjs = require('uglify-js'),
	path = require('path'),
	fs = require('fs'),
	_ = require('../../lib/alloy/underscore')._;

var JSON_NULL = JSON.parse('null');

function dotCheck(node, name) {
	return node instanceof uglifyjs.AST_Dot && node.property === name;
}

function subCheck(node, name) {
	return node instanceof uglifyjs.AST_Sub && node.property.value === name;
}

function dotSubCheck(node, name) {
	return dotCheck(node, name) || subCheck(node, name);
}

// Optimize Titanium namespaces with static strings where possible
exports.optimize = function(ast, defines, fn) {
	//var platform = {};
	var theKey;
	_.find(defines, function(value, key) {
		var ret = key.indexOf('OS_') === 0 && value;
		if (ret) { theKey = key; }
		return ret;
	});
	if (!theKey) { return ast; }

	// make sure the platform require includes
	var platformString = theKey.substring(3).toLowerCase();
	var platformPath = path.join(__dirname, '..', '..', '..', 'platforms', platformString, 'index');
	if (!fs.existsSync(platformPath + '.js')) { return ast; }

	// create, transform, and validate the platform object
	var platform = require(platformPath);
	if (!_.isString(platform.name)) { platform.name = undefined; }
	if (!_.isString(platform.osname)) { platform.osname = undefined; }
	if (!platform.osname && !platform.name) { return ast; }

	// Walk tree transformer changing (Ti|Titanium).Platform.(osname|name)
	// into static strings where possible. This will allow the following
	// compression step to reduce the code further.
	var transformer = new uglifyjs.TreeTransformer(function(node, descend) {
		var convert = false;
		if (dotSubCheck(node, 'name') || dotSubCheck(node, 'osname')) {
			descend(node, new uglifyjs.TreeTransformer(function(node, descend) {
				if (dotSubCheck(node, 'Platform')) {
					descend(node, new uglifyjs.TreeTransformer(function(node) {
						if (node instanceof uglifyjs.AST_SymbolRef &&
							(node.name === 'Titanium' || node.name === 'Ti')) {
							convert = true;
						}
						return node;
					}));
				}
				return node;
			}));
			if (convert) {
				var value = node instanceof uglifyjs.AST_Dot ? node.property : node.property.value;
				if (platform[value]) {
					return new uglifyjs.AST_String({
						start: node.start,
						end: node.end,
						value: platform[value]
					});
				} else {
					return node;
				}
			}
			return node;
		}
	});
	return ast.transform(transformer);
};

// strips null and undefined values out of Alloy styles
exports.optimizeStyle = function(styleList) {
	for (var style in styleList) {
		for (var key in styleList[style]) {
			var v = styleList[style][key];
			if (v == JSON_NULL || typeof(v) === 'undefined' || typeof(v) === 'null') {
				delete styleList[style][key];
			}
		}
	}
};

var fs = require('fs'),
	path = require('path'),
	wrench = require('wrench'),
	CONST = require('../../common/constants'),
	logger = require('../../logger');

var dir, file, projectPath;

function BuildLog(_projectPath) {
	// make/reference singleton instance
	if (BuildLog.instance) {
		return BuildLog.instance;
	}
	BuildLog.instance = this;

	// set "private" variables
	projectPath = _projectPath;
	dir = path.join(projectPath, CONST.DIR.BUILD);
	file = path.join(dir, 'build.json');

	// expose data object
	this.isNew = true;
	this.data = {};

	// make sure the alloy build folder exists
	if (!fs.existsSync(dir)) {
		wrench.mkdirSyncRecursive(dir, 0755);
	}

	// load it up
	this.read();
}

BuildLog.prototype.read = function() {
	if (!fs.existsSync(file)) {
		this.isNew = true;
		this.data = {};
	} else {
		this.isNew = false;
		try {
			this.data = JSON.parse(fs.readFileSync(file, 'utf8'));
		} catch (e) {
			logger.warn('Build log at "' + path.relative(projectPath, file) +
				'" is corrupt, creating a new one...');
			this.data = {};
		}
	}
};

BuildLog.prototype.write = function() {
	try {
		fs.writeFileSync(file, JSON.stringify(this.data));
	} catch (e) {
		logger.warn('Unable to write build log to "' + path.relative(projectPath, file) + '"');
	}
};

module.exports = BuildLog;

var logger = require('../../logger'),
	colors = require('colors');

function CompilerMakeFile() {
	var handlers = {};

	this.require = require;
	this.process = process;

	this.task = function(event, fn) {
		logger.debug('adding task: ' + event.yellow);
		handlers[event] = fn;
	};

	this.trigger = function(event, config) {
		logger.debug('compile:trigger-> ' + event.yellow);
		var fn = handlers[event];
		if (fn) {
			return fn(config, logger);
		}
		return null;
	};

	return this;
}

module.exports = CompilerMakeFile;

/*
	Speeds compilation by not re-copying, re-generating, or deleting some files
	on subsequent compiles. For example, the base client-side lib, alloy.js, is
	not deleted and recopied after the initial compile.
*/
var fs = require('fs'),
	path = require('path'),
	wrench = require('wrench'),
	platforms = require('../../../platforms/index'),
	logger = require('../../logger'),
	CONST = require('../../common/constants'),
	U = require('../../utils'),
	_ = require('../../lib/alloy/underscore');

var ALLOY_ROOT = path.join(__dirname, '..', '..');

var dirs, platform, titaniumFolder, theme, adapters;
var widgetsInUse = {};

function Orphanage(projectDir, _platform, opts) {
	opts = opts || {};
	platform = _platform;
	titaniumFolder = platforms[platform].titaniumFolder;
	theme = opts.theme;
	adapters = opts.adapters || [];

	// gather directories to be used throughout Orphanage
	var resourcesDir = path.join(projectDir, CONST.RESOURCES_DIR);
	dirs = {
		app: path.join(projectDir, CONST.ALLOY_DIR),
		resources: path.join(projectDir, CONST.RESOURCES_DIR),
		runtime: path.join(resourcesDir, platforms[platform].titaniumFolder, CONST.ALLOY_RUNTIME_DIR)
	};

	// get widgets in use
	_.each(U.getWidgetDirectories(dirs.app) || [], function(wObj) {
		widgetsInUse[path.basename(wObj.dir)] = wObj.dir;
	});
}
module.exports = Orphanage;

Orphanage.prototype.clean = function() {
	var that = this;

	// Clean the base app folder
	this.removeAll();

	// get rid of unused adapters
	logger.debug('Removing orphaned sync adapters...');
	this.removeAdapters();

	// Clean out each widget
	var widgets = path.join(dirs.runtime, CONST.DIR.WIDGET);
	if (fs.existsSync(widgets)) {
		_.each(fs.readdirSync(widgets), function(file) {
			if (!widgetsInUse[file]) {
				that.removeAll({ widgetId: file });
			}
		});
	}

	// assets must be cleaned up last
	logger.debug('Removing orphaned assets and libs...');
	this.removeAssets();
};

// TODO: handle specs
Orphanage.prototype.removeAll = function(opts) {
	opts = opts || {};

	if (!opts.widgetId) {
		logger.debug('Removing orphaned controllers ...');
		this.removeControllers(opts);

		logger.debug('Removing orphaned models ...');
		this.removeModels(opts);

		logger.debug('Removing orphaned styles ...');
		this.removeStyles(opts);
	} else {
		this.removeWidget(opts);
	}
};

Orphanage.prototype.removeAdapters = function(opts) {
	opts = _.clone(opts || {});
	var paths = [
		path.join('alloy', 'sync'),
		path.join(titaniumFolder, 'alloy', 'sync')
	];

	_.each(paths, function(p) {
		var adapterDir = path.join(dirs.resources, p);
		if (!fs.existsSync(adapterDir)) {
			return;
		}

		_.each(fs.readdirSync(adapterDir), function(adapterFile) {
			var fullpath = path.join(adapterDir, adapterFile);
			var adapterName = adapterFile.replace(/\.js$/, '');
			if (!_.contains(adapters, adapterName) && fs.statSync(fullpath).isFile()) {
				logger.trace('* ' + path.join(p, adapterFile));
				fs.unlinkSync(fullpath);
			}
		});
	});
};

Orphanage.prototype.removeControllers = function(opts) {
	opts = _.clone(opts || {});
	remove(_.extend(opts, {
		folder: CONST.DIR.CONTROLLER,
		types: ['CONTROLLER', 'VIEW'],
		exceptions: ['BaseController.js']
	}));
};

Orphanage.prototype.removeModels = function(opts) {
	opts = _.clone(opts || {});
	remove(_.extend(opts, {
		folder: CONST.DIR.MODEL,
		types: 'MODEL'
	}));
};

Orphanage.prototype.removeStyles = function(opts) {
	opts = _.clone(opts || {});
	remove(_.extend(opts, {
		folder: CONST.DIR.STYLE,
		types: ['CONTROLLER', 'VIEW']
	}));
};

Orphanage.prototype.removeAssets = function() {
	var baseLocations = [
		CONST.DIR.ASSETS,
		path.join(CONST.DIR.ASSETS, platforms[platform].titaniumFolder),
		CONST.DIR.LIB,
		CONST.DIR.VENDOR
	];
	var locations = [];

	// Add the base locations
	_.each(baseLocations, function(loc) {
		var newLoc = path.join(dirs.app, loc);
		if (fs.existsSync(newLoc)) {
			locations.push(newLoc);
		}
	});

	// Make sure we check the widgets paths as well
	_.each(widgetsInUse, function(wDir, wId) {
		_.each(baseLocations, function(loc) {
			var widgetLoc = path.join(wDir, loc);
			if (fs.existsSync(widgetLoc)) {
				locations.push(widgetLoc);
			}
		});
	});

	// add theme locations, if necessary
	if (theme) {
		locations.push(
			path.join(dirs.app, CONST.DIR.THEME, theme, CONST.DIR.ASSETS),
			path.join(dirs.app, CONST.DIR.THEME, theme, CONST.DIR.ASSETS, platform)
		);
	}

	// files to skip
	var exceptions = [
		'app.js',
		'alloy.js',
		'alloy',
		'alloy/*'
	];

	// check the current platform as well
	_.each(_.clone(exceptions), function(ex) {
		exceptions.push(path.join(platforms[platform].titaniumFolder, ex));
	});

	// don't worry about cleaning platforms that aren't the current target
	_.each(_.without(_.pluck(platforms, 'titaniumFolder'), platforms[platform].titaniumFolder),
		function(tf) {
			exceptions.unshift(tf + '*');
		}
	);

	remove({
		runtimePath: dirs.resources,
		locations: locations,
		exceptions: exceptions
	});
};

Orphanage.prototype.removeWidget = function(opts) {
	if (opts.widgetId === '.DS_Store') { return; }
	opts.runtimePath = path.join(dirs.runtime, CONST.DIR.WIDGET, opts.widgetId);
	remove(opts);
};

// private function
function extension(file, newExt) {
	return file.replace(/[^\.]+$/, newExt);
}

function getChecks(file, fullpath, opts) {
	opts = opts || {};
	var isDir = fs.statSync(fullpath).isDirectory();
	var checks = [];

	// Check all the app folder file types
	if (opts.types) {
		_.each(opts.types, function(type) {
			var dir = CONST.DIR[type];

			// use type-specific extension if it's not a directory
			if (!isDir) {
				file = extension(file, CONST.FILE_EXT[type]);
			}

			// use widget path, if it's a widget
			if (opts.widgetId) {
				dir = path.join(CONST.DIR.WIDGET, opts.widgetId, dir);
			}

			// create the file checks
			checks.push(
				path.join(dirs.app, dir, file),
				path.join(dirs.app, dir, platform, file)
			);
		});

	// use explicit full location paths
	} else if (opts.locations) {
		// strip off the platform if present
		var parts = file.split(/[\\\/]/);
		if (parts[0] === titaniumFolder) {
			parts.shift();
		}
		file = parts.join('/');

		// Is it a widget file?
		var keys = _.keys(widgetsInUse);
		for (var i = 0; i < keys.length; i++) {
			var widgetId = keys[i];

			// did we find the widget root path?
			if (parts.length === 1 && parts[0] === widgetId) {
				return null;
			}

			// is this a widget asset?
			if (_.contains(parts, widgetId)) {
				file = _.without(parts, widgetId).join('/');
				break;
			}
		}

		// No widget? Just use the given locations.
		_.each(opts.locations, function(loc) {
			checks.push(path.join(loc, file));
		});
	}

	return checks;
}

function isException(file, exceptions) {
	var ex, exs = [];
	exceptions = exceptions || [];

	for (var i = 0; i < exceptions.length; i++) {
		ex = exceptions[i];
		exs.push(ex);

		// handle folder wildcards
		if (ex.charAt(ex.length - 1) === '*') {
			for (var j = 0; j < exs.length; j++) {
				var newEx = exs[i].substr(0, exs[i].length - 2);

				// see if the file starts with the wildcard
				if (file.length >= newEx.length && file.substr(0, newEx.length) === newEx) {
					return true;
				}
			}

		// straight up comparison if there's no wildcards
		} else if (_.contains(exs, file)) {
			return true;
		}
	}

	return false;
}

function remove(opts) {
	opts = opts || {};
	var folder = opts.folder;
	var exceptions = opts.exceptions || [];
	var types = _.isString(opts.types) ? [opts.types] : opts.types;
	var locations = _.isString(opts.locations) ? [opts.locations] : opts.locations;
	var runtimePath = opts.runtimePath;

	// Set the base runtime search path
	if (!runtimePath) {
		if (!folder) {
			U.die([
				'You must specify either "runtimePath" or "folder" when calling Orphanage.remove()',
				new Error().stack
			]);
		} else {
			if (opts.widgetId) {
				runtimePath = path.join(dirs.runtime, CONST.DIR.WIDGET, opts.widgetId, folder);
			} else {
				runtimePath = path.join(dirs.runtime, folder);
			}
		}
	}

	// skip if the target runtime folder doesn't exist
	if (!fs.existsSync(runtimePath)) {
		return;
	}

	// Let's see if we need to delete any orphan files...
	_.each(wrench.readdirSyncRecursive(runtimePath), function(file) {
		var runtimeFullpath = path.join(runtimePath, file);
		var found = false;
		var checks, i;

		// skip if file no longer exists, or if it's an exception
		if (!fs.existsSync(runtimeFullpath) ||
			(/*!opts.widgetId && */ isException(file, exceptions))) {
			return;
		}

		// Get a list of app folder locations to check for a match against the runtime file
		checks = getChecks(file, runtimeFullpath, _.extend({ widgetId: opts.widgetId },
			types ? { types: types } : { locations: locations }));

		// if checks is null, we already know we can skip it
		if (checks === null) {
			return;
		}

		// If we find the corresponding app folder file(s), skip this file
		for (i = 0; i < checks.length; i++) {
			if (!opts.widgetId && fs.existsSync(checks[i])) {
				found = true;
				return;
			}
		}

		// It's an orphan, delete it
		if (!found) {
			// already deleted, perhaps a file in a deleted directory
			if (!fs.existsSync(runtimeFullpath)) { return; }
			logger.trace('* ' + file);

			// delete the directory or file
			var targetStat = fs.statSync(runtimeFullpath);
			if (targetStat.isDirectory()) {
				if (opts.widgetId) {
					// remove the widget's folder
					wrench.rmdirSyncRecursive(path.resolve(runtimeFullpath, '..'), true);
				} else {
					wrench.rmdirSyncRecursive(runtimeFullpath, true);
				}
			} else {
				fs.unlinkSync(runtimeFullpath);
			}
		}
	});
}

var U = require('../../utils'),
	logger = require('../../logger'),
	path = require('path'),
	_ = require('../../lib/alloy/underscore')._,
	XMLSerializer = require('xmldom').XMLSerializer,
	fs = require('fs'),
	wrench = require('wrench'),
	os = require('os');

var FILE_TEMPLATE = '<?xml version="1.0" encoding="UTF-8"?>' + os.EOL + '<resources>' +
	os.EOL + '</resources>';

function check(strings) {
	if (!strings || strings.length === 0) {
		logger.warn('No new i18n strings found. Nothing to do.');
		process.exit(0);
	}
}

module.exports = function(projectRoot, language) {
	var i18nDir = path.join(projectRoot, 'app', 'i18n', language);
	var i18nFile = path.join(i18nDir, 'strings.xml');
	var shortPath = path.relative(projectRoot, i18nFile);

	// create 18n folder if it doesn't exist
	if (!fs.existsSync(i18nDir)) {
		wrench.mkdirSyncRecursive(i18nDir, 0755);
	}

	// create i18n file if it doesn't exist
	if (!fs.existsSync(i18nFile)) {
		fs.writeFileSync(i18nFile, FILE_TEMPLATE, 'utf8');
	}

	var api = {
		process: function(strings) {
			var doc = U.XML.parseFromFile(i18nFile);
			var root = doc.documentElement;

			// create a <string> node for each new string key
			_.each(strings, function(str) {
				var node = doc.createElement('string');
				var value = doc.createTextNode(str);
				node.setAttribute('name', str);
				root.appendChild(doc.createTextNode('  '));
				node.appendChild(value);
				root.appendChild(node);
				root.appendChild(doc.createTextNode('\n'));
			});

			// serialize the document and write it back to the strings.xml file
			var serializer = new XMLSerializer();
			return serializer.serializeToString(doc);
		},

		// Get an array of all localization string keys not already in the strings.xml file
		merge: function(strings) {
			var doc = U.XML.parseFromFile(i18nFile);
			var oldStrings = _.map(doc.getElementsByTagName('string'), function(node) {
				return node.attributes.getNamedItem('name').nodeValue;
			});
			return _.difference(strings, oldStrings);
		},

		// Print the XML that would be written to your strings.xml file with --apply
		print: function(strings) {
			check(strings);
			logger.info('######## BEFORE ########' + os.EOL + api.process([]));
			logger.info('######## AFTER  ########' + os.EOL + api.process(strings));
			logger.info(' ');
			logger.warn('Did not write the "' + shortPath + '" file - use "--apply" option to write.');
		},

		// Append any new localization strings to the existing strings.xml file
		write: function(strings) {
			check(strings);
			fs.writeFileSync(i18nFile, api.process(strings), 'utf8');
			logger.info(('Wrote strings in the "' + path.relative(projectRoot, i18nFile) + '" file.').green);
		}
	};

	return api;
};

var basePath = '../../';
var path = require('path'),
	fs = require('fs'),
	wrench = require('wrench'),
	xml2tss = require('xml2tss'),
	alloyRoot = path.join(__dirname, '..', '..'),
	_ = require(basePath + 'lib/alloy/underscore')._,
	U = require(basePath + 'utils'),
	CONST = require(basePath + 'common/constants'),
	logger = require(basePath + 'logger');

function pad(x) {
	if (x < 10) {
		return '0' + x;
	}
	return x;
}

exports.generateMigrationFileName = function(t) {
	var d = new Date();
	var s = String(d.getUTCFullYear()) + String(pad(d.getUTCMonth() + 1)) +
		String(pad(d.getUTCDate())) + String(pad(d.getUTCHours())) +
		String(pad(d.getUTCMinutes())) + String(d.getUTCMilliseconds());
	return s + '_' + t;
};

exports.generate = function(name, type, program, args) {
	args = args || {};
	var ext = '.' + CONST.FILE_EXT[type];
	var paths = U.getAndValidateProjectPaths(program.outputPath, {command : CONST.COMMANDS.GENERATE});
	var templatePath = path.join(alloyRoot, 'template', type.toLowerCase() + ext);
	// ALOY-372 - Support 'alloy generate' command for widget components
	var widgetPath = (program.widgetname) ? CONST.DIR['WIDGET'] + path.sep + program.widgetname : '';
	if (widgetPath && !fs.existsSync(path.join(paths.app, widgetPath))) {
		U.die('No widget named ' + program.widgetname + ' in this project.');
	}
	var dir = path.join(paths.app, widgetPath, CONST.DIR[type]);

	// add the platform-specific folder to the path, if necessary
	if (program.platform) {
		if (_.contains(['VIEW', 'CONTROLLER', 'STYLE'], type)) {
			dir = path.join(dir, program.platform);
		} else {
			logger.warn('platform "' + program.platform +
				'" ignored, not used with type "' + type + '"');
		}
	}

	// get the final file name
	var file = path.join(dir, name + ext);
	var viewFile = path.join(paths.app, CONST.DIR['VIEW'], name + '.' +
		CONST.FILE_EXT['VIEW']);

	// see if the file already exists
	if (fs.existsSync(file) && !program.force &&
		!(type === 'STYLE' && fs.existsSync(viewFile))) {
		U.die(' file already exists: ' + file);
	}

	// make sure the target folder exists
	var fullDir = path.dirname(file);
	if (!fs.existsSync(fullDir)) {
		wrench.mkdirSyncRecursive(fullDir);
	}

	// only use xml2tss to generate style if the partner view exists
	if (type === 'STYLE' && fs.existsSync(viewFile)) {
		xml2tss.updateFile(viewFile, file, function(err, ok) {
			if (ok) {
				logger.info('Generated style named ' + name);
			} else {
				logger.warn('Style named ' + name + ' already up-to-date');
			}
		});
	} else {
		// write the file out based on the given template
		var templateContents = fs.readFileSync(templatePath, 'utf8');
		if (args.templateFunc) {
			templateContents = args.templateFunc(templateContents);
		}
		var code = _.template(templateContents, args.template || {});
		fs.writeFileSync(file, code);

		return {
			file: file,
			dir: fullDir,
			code: code
		};
	}
};

var Alloy = require('/alloy');

// Hold a collection of widget objects instances. These
// objects are not the widgets themselves, but a set of
// auto-populated functions and properties that make
// developing widgets easier.
var widgets = {};

function ucfirst(text) {
	if (!text) { return text; }
	return text[0].toUpperCase() + text.substr(1);
}

module.exports = function(widgetId) {
	var self = this;

	// return existing widget object, if present
	if (widgets[widgetId]) {
		return widgets[widgetId];
	}

	// properties
	this.widgetId = widgetId;
	this.Collections = {};
	this.Models = {};
	this.Shared = {};

	// functions
	this.createController = function(name, args) {
		return new (require('/alloy/widgets/' + widgetId + '/controllers/' + name))(args);
	};
	this.createCollection = function(name, args) {
		return new (require('/alloy/widgets/' + widgetId + '/models/' + ucfirst(name)).Collection)(args);
	};
	this.createModel = function(name, args) {
		return new (require('/alloy/widgets/' + widgetId + '/models/' + ucfirst(name)).Model)(args);
	};
	this.createWidget = Alloy.createWidget; // just to be complete
	this.Collections.instance = function(name) {
		return self.Collections[name] || (self.Collections[name] = self.createCollection(name));
	};
	this.Models.instance = function(name) {
		return self.Models[name] || (self.Models[name] = self.createModel(name));
	};

	// add to widget object instances
	widgets[widgetId] = this;
};

function addItem() {
	var todos = Alloy.Collections.todo;

	// Create a new model for the todo collection
	var task = Alloy.createModel('Todo', {
		item : $.itemField.value,
		done : 0
	});

	// add new model to the global collection
	todos.add(task);

	if (task.isValid()) {
		// save the model to persistent storage
		task.save();
	} else {
		task.destroy();
	}

	// reload the tasks
	todos.fetch();

	closeWindow();
}

function focusTextField() {
	$.itemField.focus();
}

function closeKeyboard(e) {
	e.source.blur();
}

function closeWindow() {
	$.addWin.close();
}

var moment = require('alloy/moment');
var todos = Alloy.Collections.todo;
var id;

// $model represents the current model accessible to this
// controller from the markup's model-view binding. $model
// will be null if there is no binding in place.

if ($model) {
	id = $model.id;
	if ($model.get('done')) {
		$.row.backgroundColor = '#eee';
		$.check.backgroundColor = '#eee';
		$.task.color = '#ccc';
		$.check.image = '/tick_64.png';
	} else {
		$.row.backgroundColor = '#fff';
		$.check.backgroundColor = '#fff';
		$.task.color = '#000';
		$.check.image = '/tick_gray_64.png';
	}
}

// toggle the 'done' status of the IDed todo
function toggleStatus(e) {
	// find the todo task by id
	var todo = todos.get(id);

	// set the current 'done' and 'date_completed' fields for the model,
	// then save to presistence, and model-view binding will automatically
	// reflect this in the tableview
	todo.set({
		'done': todo.get('done') ? 0 : 1,
		'date_completed': moment().unix()
	}).save();
}

// delete the IDed todo from the collection
function deleteTask(e) {
	// prevent bubbling up to the row
	e.cancelBubble = true;

	// find the todo task by id
	var todo = todos.get(id);

	// destroy the model from persistence, which will in turn remove
	// it from the collection, and model-view binding will automatically
	// reflect this in the tableview
	todo.destroy();
}

